import React, {useEffect, useState} from 'react'
import { useParams, Link } from 'react-router-dom'
import axios from 'axios'

const {REACT_APP_GET_ALL_COUNTRIES} = process.env

const Detail = () => {

    const { id } = useParams()

    const [character, setCharacter] = useState({});
    
    useEffect(() =>{
        axios.get(`REACT_APP_GET_ALL_COUNTRIES/${id}`)
        .then((response) => setCharacter(response.data))
    },[id])

    const flag = character.flags.replace(/[{}]/g, '').split(',')[0]
    

  return (
    <div>
        <div>
            <h1>{character.name}</h1>
            <img src={flag} alt={character.name} height='100px' width='200px'/>
            <h2>{character.continents}</h2>
            <h2>{character.capital === null ? 'N/A' : character.capital.replace(/[{}]/g, '')}</h2>
            <h1>{character.population}</h1>
        </div>
        <Link to={'/home'}>Home</Link>
        
    </div>
  )
}

export default Detail
