import React, {useEffect, useState} from 'react'
import { Link } from 'react-router-dom'
import axios from 'axios'
import style from './CreatActivity.module.css'

const {REACT_APP_GET_ALL_COUNTRIES} = process.env

const CreatActivity = () => {
    
    const [countries, setCountries] = useState([])
    const [selectedOptions, setSelectedOptions] = useState([])

    useEffect(() =>{
        async function fetchData(){
            try {
                const response = await axios.get(REACT_APP_GET_ALL_COUNTRIES)
                const data = response.data

                setCountries(data)
            } catch (error) {
                console.log(error)
            }
        }
        fetchData()
    },[])

    const handleOptionToggle = value =>{
        if(selectedOptions.includes(value)){
            setSelectedOptions(selectedOptions.filter(opt => opt !== value))
        }else{
            setSelectedOptions([...selectedOptions, value])
        }
    }
    
  return (
    <div>
        <h1> Create Activity </h1>

        <div>
            <form className={style.createForm} action="submit">
                <label htmlFor="">Name:</label>
                <input></input>
                <label htmlFor="">Difficulty:</label>
                <select name="difficultySelect" id="difficultySelect">
                    <option>--Select difficulty--</option>
                    <option value="1">1- Easy</option>
                    <option value="2">2- Medium</option>
                    <option value="3">3- Hard</option>
                    <option value="4">4- Extreme</option>
                </select>
                <label htmlFor="">Duration:</label>
                <select name="durationSelect" id="durationSelect">
                    <option>--Select dduration--</option>
                    <option value="1">1 hour</option>
                    <option value="2">2 hour</option>
                    <option value="3">3 hour</option>
                    <option value="4">4 hour and more</option>
                </select>
                <label htmlFor="">Country:</label>
                <select 
                    id='countriesSelect' 
                    multiple
                    name='countriesSelect'
                    value={selectedOptions}
                    onChange={(e) => handleOptionToggle(e.target.value)}
                >
                    {countries && countries.map(country =>{
                        return <option value={country.name}>{country.name}</option>
                    })}
                </select>
                <button type='submit'>Create</button>
            </form>
        </div>
        <Link to='/home'>
            <button>Home</button>
        </Link>
    </div>
  )
}

export default CreatActivity
