import React, {useEffect, useState} from 'react'
import {useParams} from 'react-router-dom'
import axios from 'axios'

const {REACT_APP_GET_ALL_COUNTRIES} = process.env

const Detail = () => {

  const { id } = useParams()
  const [character, setCharacter] = useState({})
  const [loading, setLoading] = useState(true)

  useEffect(() =>{
    async function fetchData(){
      try {
        const response = await axios.get(`${REACT_APP_GET_ALL_COUNTRIES}/id/${id}`)
        const data = response.data
        const capital = data.capital.replace(/[{}""]/g, '').split(',')
        setCharacter({
          name : data.name,
          flag: data.flags,
          continent: data.continents,
          capital: capital[0],
          population: data.population
        })
        
      } catch (error) {
        console.error(error)
      }
    }

    fetchData()
  },[id])

  return loading ? (
    <div>
      <h1>{character.name}</h1>
      <h1>{character.continent}</h1>
      <h1>{character.capital}</h1>
      <h1>{character.population}</h1>
      <img src={character.flag} alt={character.name} />
    </div>
  ) : (<h1>Loading</h1>)
}

export default Detail
